def reName(ori_name, rep1, rep2, prefix):
    if rep1 == 'contig':
        my_name = re.sub('contig_', rep2, ori_name)
    else:
        my_name = re.sub('clonotype',rep1, ori_name)
        my_name = re.sub('consensus_', rep2, my_name)
    if prefix != '':
        my_name = prefix + '_' + my_name
    return my_name

