# calculate AA Similarity based on BLOSUM62 matrix
def AASimilarity(AA):
    # Proc Natl Acad Sci U S A. 1992 Nov 15;89(22):10915-9.
    # Amino acid substitution matrices from protein blocks.
    # Henikoff S1, Henikoff JG.
    # Based on above reference and the BLOSUM62 similarity indices
    # BLOSUM62 = {}
    ExScore = ''
    try:
        if AA in BLOSUM62:
            ExScore = BLOSUM62[AA]
        else:
            AA = AA[1]+ AA[0]
            ExScore = BLOSUM62[AA]
    except:
            ExScore = ''
    return ExScore

# calculate consensus
def CalConsensus(inputlist):
    try:
        consensusSeq = ''
        seqLen = len(inputlist[0])
        for i in range(seqLen):
            curStr = []
            for seq in inputlist:
                if seq[i] == '-':
                    pass
                else:
                    curStr.append(seq[i])
            if len(curStr) > 0:
                maxStr = max(curStr, key=curStr.count)
            else:
                maxStr = '-'
            consensusSeq += maxStr
        return consensusSeq
    except:
        return "bad"

